-- Create a table with specific columns and types for products
CREATE TABLE rvbs (
    id INT AUTO_INCREMENT NOT NULL,
    ref_fnr VARCHAR(255),
    ref_int VARCHAR(255),
    famille VARCHAR(255),
    sous_famille VARCHAR(255),
    designation VARCHAR(255),
    specificite VARCHAR(255),
    dimension VARCHAR(255),
    couleur VARCHAR(255),
    h5 INT,
    h3 INT, 
    h4 INT,
    h8 INT,
    h7 INT,
    total INT,
    qte_plus_10 INT,
    prepare INT,
    charge INT,
    commander INT,
    location INT,
    stock INT,
    slode_stock INT,
    observation TEXT,
    inventaire_fait_le DATE,
    PRIMARY KEY (id)
);

